Queries.BOT_InsertDocuments:
CREATE PROCEDURE "BOT_InsertDocuments" ( prmProveedorTechnologico NVARCHAR(50), prmConsecutivoTFHKA NVARCHAR(50), prmCUFE NVARCHAR(250), prmStatusDIANCodigo NVARCHAR(50), prmStatusDIANDescripcion NVARCHAR(50), prmStatusDIANFecha NVARCHAR(50), prmFechaEmision NVARCHAR(50), prmFechaRecepcion NVARCHAR(50), prmHoraEmision NVARCHAR(50), prmMontoTotal NVARCHAR(50), prmNumeroFactura NVARCHAR(50), prmNumeroIdentificacion NVARCHAR(50), prmRazonSocial NVARCHAR(250), prmTipoDocumento NVARCHAR(50), prmTipoEmisor NVARCHAR(50), prmTipoIdentidad NVARCHAR(50), prmCodEstatusDIAN NVARCHAR(50)
)
LANGUAGE SQLSCRIPT AS
Validation INT; Consecutivo NVARCHAR(20);
BEGIN
SELECT COUNT(*) INTO Validation FROM "@BOTREDRPT" WHERE "U_BOTNF" = :prmNumeroFactura AND "U_BOTNI" = :prmNumeroIdentificacion;
IF :Validation = 0
THEN
SELECT (CAST(RIGHT('0000000000' || Ltrim(Rtrim(COUNT("Code")+1)),10) AS NVARCHAR(100))) INTO Consecutivo FROM "@BOTREDRPT";
INSERT INTO "@BOTREDRPT" VALUES ( :Consecutivo, :Consecutivo,--CAST(RIGHT('0000000000' || Ltrim(Rtrim(COUNT("Code")+1)),10) AS NVARCHAR(100)), :prmProveedorTechnologico, :prmConsecutivoTFHKA, :prmCUFE, :prmStatusDIANCodigo, :prmStatusDIANDescripcion, :prmStatusDIANFecha, :prmFechaEmision, :prmFechaRecepcion, :prmHoraEmision, :prmMontoTotal, :prmNumeroFactura, :prmNumeroIdentificacion, :prmRazonSocial, :prmTipoDocumento, :prmTipoEmisor, :prmTipoIdentidad, :prmCodEstatusDIAN, NULL, NULL, NULL, NULL, NULL, NULL, NULL
);
END IF;
END;

Queries.BOT_SyncAttachment:
    CREATE PROCEDURE "BOT_SyncAttachment"
      (
		prmTipoAdjunto NVARCHAR(50),
		pmrFile NVARCHAR(500),
		prmNIT NVARCHAR(250),
		prmNF NVARCHAR(50),
		CRWS NVARCHAR(50)		
		)    

    LANGUAGE SQLSCRIPT AS

	    ExisFile INT;

    BEGIN

    	SELECT COUNT(*) INTO  ExisFile FROM "@BOTREDRPT" WHERE "U_BOTNF" = :prmNF AND "U_BOTNI" = :prmNIT AND "U_BOTCWSPDF" IS NULL ;

		 IF(:ExisFile > 0)
		 	THEN	 		
		 		IF (:prmTipoAdjunto = 'PDF')
			 		THEN
			 			UPDATE "@BOTREDRPT"
			    		SET "U_BOTPAXML" = :pmrFile
			    		WHERE "U_BOTNF" = :prmNF AND "U_BOTNI" = :prmNIT;
			 		END IF;
			 	ELSE IF (:prmTipoAdjunto = 'XML')
			 		THEN
			 			UPDATE "@BOTREDRPT"
			    		SET "U_BOTPAPDF" = :pmrFile
			    		WHERE "U_BOTNF" = :prmNF AND "U_BOTNI" = :prmNIT; 
			 		END IF ; 	 		
		 	END IF;    

  	END;
